mapper.insert:

	select max(bno)+1 bno from tb1_board;


insert into tb1_board(bno, title, content, writer) 
values(#{bno}, #{title}, #{content}, #{writer})

mapper.update:
 			 update tb1_board
  				set title = #{title}
    	        , content = #{content}
            , updatedate = sysdate()     
			 where   
      			   bno = #{bno}

			 update tb1_board
  			set replycnt = replycnt + #{amount}
			  where bno = #{bno}
